var twilio = require('twilio');
var nodemailer = require('nodemailer');

module.exports.sendAuthCode = function(authCode, medium){
		switch(medium){
			case 'sms':
				sendSms(authCode)
				break;

			case 'email':
				sendEmail(authCode)
				break;

			default:
				console.log(authCode)			
		}
	}

